<style scoped lang="scss">
    .sidebar {
        background-color: var(--background-contrast);
        max-width: 280px;
        width: 100%;
        height: 100vh;
        padding: 20px 16px;
        border-right: 1px solid rgba(145, 158, 171, 0.24);
        flex-shrink: 0;
        position: sticky;
        top: 0;
        z-index: 100;
        .mobileButton {
            display: none;
        } 
        @media (max-width: 992px) {
            position: fixed;
            left: -280px;
            transition:left .5s;
            top: 0;
            .mobileButton {
                display: block;
                position: absolute;
                left: calc(100% + 1px);
                padding: 24px 12px;
                border-radius: 0 6px 6px 0;
                background-color: var(--background-contrast);
                box-shadow: 3px 3px 12px rgba(0,0,0,.08);
                top: 12%;
                svg {
                    transition: transform .5s;
                }
            } 
            &.open {
                left: 0px;
                .mobileButton {
                    svg {
                        transform: rotate(-180deg);
                    }
                }
            }
        }
        .sidebarLogo {
            display: block;
            width: 40px;
            height: 40px;
            margin:0 0 39px;

            img {
                width: 100%;
                height: 100%;
            }
        }
        nav {
            a {
                display: flex;
                gap: 16px;
                align-items: center;
                padding: 13px 16px;
                border-radius: 8px;
                margin: 0 0 4px;
                &.router-link-active {
                    background-color: var(--primary-highlights);
                }
                @media (pointer: fine) and (hover: hover) {
                    &:hover {
                        background-color: var(--hover);
                    }
                }
            }
        }
    }
</style>
<script>
import { ref } from 'vue';
export default {
    setup() {
        let sideBarState = ref(false);

        let toggleSideBar = () => {
            sideBarState.value = !sideBarState.value ? true : false;
        }
        
        return {
            toggleSideBar,
            sideBarState
        }
    }
}
</script>